mapper.select:
select m.member_id as memberId , m2.name as name , p.product_img as img , p.product_price as price , c.product_quantity as qty , p.product_title as title , p.product_delivery_charge as deliveryCharge , c.shopping_cart_id as cartId
from shopping_cart as c
join member m on c.member_id = m.member_id join product p on c.product_id = p.product_id join member m2 on p.member_id = m2.member_id where c.member_id = #{memberId}

select member_id as memberId
from shopping_cart
where shopping_cart_id = #{cartId}
