mapper.select:
select count(*)
from review
where product_id = ${productId}

select review_id as reviewId, review_grade as reviewGrade, review_img as reviewImg, review_contents as reviewContents, review_created_date as reviewCreatedDate, product_id as productId, m.account_id as accountId, help as help
from review as r inner join member as m on r.member_id = m.member_id where r.product_id = ${productId}
order by reviewId desc
order by reviewGrade desc
limit #{firstRecordIndex}, #{criteria.recordsPerPage}

mapper.update:
update review
set review_grade    = #{reviewGrade},
    review_img      = #{reviewImg},
    review_contents = #{reviewContents}
where review_id = #{reviewId}
